# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         self.btn_max.pack(side="right", padx=1)
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         self.btn_max.bind("<Enter>", lambda e: self.btn_max.config(bg="#444444"))
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         self.btn_max.bind("<Leave>", lambda e: self.btn_max.config(bg="#2E2E2E"))
        
