# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         x = event.x_root - self._offsetx
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         y = event.y_root - self._offsety
