# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #             if self.previous_geometry:
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #                 self.master.geometry(self.previous_geometry)
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #             self.is_maximized = False

# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #     def close(self):
# # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # # #         self.master.quit()
